import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../Redux&Store/auth';
import Card from './Card'
function Main({  setisLoggedin}) { 
  const totalQuantity=useSelector((state)=>{return state.cart .totalQuantity});
  console.log(totalQuantity);
  const dispatch=useDispatch();
  const handleLogout=()=>{
    dispatch(logout());
  }
  return (
    <>
    <div className="header">
        <h2>Redux Shopping App</h2>
        <h3>Cart:{totalQuantity} items</h3>
        <button className='logoutBtn' onClick={handleLogout}>Logout</button>
    </div>
    <Card></Card>
    <div className="footer">
    <p className='totalAmount'>Total :200$</p>
    <button className='OrderBtn'>Place Order</button>
    </div>
    </>
  )
}

export default Main
