import React from 'react'
import "./style.css";
import { useSelector,useDispatch} from 'react-redux';
import {increNumber,decreNumber} from "./actions/index";
function App() {

  // useselector selects the reducer we need
  const mystate=useSelector((state)=>{
    return state.changeTheNumber;
  })
  const dispatch=useDispatch();

  return (
    <div className="container">

      <div className="quantity">
        <h3>Increment/Decrement counter</h3>
        <h4>Using Redux and React</h4>
        <div className="buttons">
          <button className='decrment' onClick={()=>dispatch(increNumber)}>-</button>
          <input type="text" value={mystate} name="value"></input>  
          <button className='increment' onc>+</button>

        </div>
      </div>
    </div>
  )
}

export default App
