import React from 'react'
import "./style.css";
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
function App() {

  const mystate=useSelector((state)=>{
    return state.changeTheNumber;
  })

  const dispatch=useDispatch();
  console.log(dispatch);

  function increment(){
        dispatch({type:"INCREMENT"})
  }
  function decrement(){
    dispatch({type:"DECREMENTMENT"})
  }
  return (
    <div className="container">

      <div className="quantity">
        <h3>Increment/Decrement counter</h3>
        <h4>Using Redux and React</h4>
        <div className="buttons">
          <button className='decrment' onClick={decrement()}>-</button>
          <input type="text" value={mystate} name="value"></input>  
          <button className='increment' onClick={increment()}>+</button>

        </div>
      </div>
    </div>
  )
}

export default App
