import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import "./style.css";
function App() {

  // useSelector is used to access variables 
const count=useSelector((state)=>{
  // counter is name given in store
    return state.counter;
})

// dispatch is used to access function
const dispatch=useDispatch();

  return (
    <div className="container">
      <div className="quantity">
        <h3>Increment/Decrement counter</h3>
        <h4>Using Redux and React</h4>
        <div className="buttons">
          <button className='decrment'onClick={dispatch(count.increment())}>-</button>
          <input type="text" value={count.count} name="value"></input>  
          <button className='increment'>+</button>

        </div>
      </div>
    </div>
  )
}

export default App
