const initialData=0;

const changeTheNumber=(state={initialData},action)=>{
  
  
    switch(action.type){
        case "INCREMENT" :return state=state+action.payload; 
        case "DECREMENT" :return state=state-action.payload; 
        default:return state.initialData;
        
    }


}

export default changeTheNumber;
